using Microsoft.EntityFrameworkCore;
using VMS.Data;
using VMS.Models;
using VMS.Models.DTO;
using VMS.Repository;
using NUnit.Framework;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace UnitTest
{
    [TestFixture]
    public class VisitorFormRepositoryTests
    {
        private VisitorManagementDbContext _context;
        private VisitorFormRepository _repository;

        [SetUp]
        public void SetUp()
        {
            var options = new DbContextOptionsBuilder<VisitorManagementDbContext>()
                .UseInMemoryDatabase(databaseName: "VisitorManagementDb")
                .Options;

            _context = new VisitorManagementDbContext(options);
            _repository = new VisitorFormRepository(_context);

            // Seed the database with some data for testing
            SeedDatabase();
        }

        [TearDown]
        public void TearDown()
        {
            _context.Database.EnsureDeleted();
            _context.Dispose();
        }

        private void SeedDatabase()
        {
            var purposes = new List<PurposeOfVisit>
            {
                new PurposeOfVisit { Id = 1, Name = "Meeting", Status = 1, CreatedBy = 1, CreatedDate = DateTime.Now },
                new PurposeOfVisit { Id = 2, Name = "Interview", Status = 1, CreatedBy = 1, CreatedDate = DateTime.Now }
            };

            var locations = new List<OfficeLocation>
            {
                new OfficeLocation { Id = 1, Name = "Main Office", Address = "123 Main St", Phone = "123-456-7890", Status = 1, CreatedBy = 1, CreatedDate = DateTime.Now },
                new OfficeLocation { Id = 2, Name = "Head Office", Address = "123 DownTown St", Phone = "123-987-7890", Status = 1, CreatedBy = 1, CreatedDate = DateTime.Now }
            };

            var devices = new List<Device>
            {
                new Device { Id = 1, Name = "Laptop", Status = 1, CreatedBy = 1, CreatedDate = DateTime.Now },
                new Device { Id = 2, Name = "Phone", Status = 1, CreatedBy = 1, CreatedDate = DateTime.Now }
            };

            var visitors = new List<Visitor>
            {
                new Visitor {  Id = 1, Name = "Visitor1", Phone = "1111111111", VisitDate = DateTime.Today,
                    HostName = "Host1", PurposeId = 1, OfficeLocationId = 1, StaffId = 1,
                    CheckInTime = DateTime.Now.AddHours(-1), CheckOutTime = DateTime.Now.AddMinutes(-30),
                    Photo = new byte[] { 0x20 }},
                new Visitor { Id = 2, Name = "Visitor2", Phone = "2222222222", VisitDate = DateTime.Today,
                    HostName = "Host2", PurposeId = 2, OfficeLocationId = 2, StaffId = 2,
                    CheckInTime = DateTime.Now.AddHours(-2), CheckOutTime = DateTime.Now.AddMinutes(-60),
                    Photo = new byte[] { 0x20 } }
            };

            var visitorDevices = new List<VisitorDevice>
            {
                new VisitorDevice { Id = 1, VisitorId = 1, DeviceId = 1, SerialNumber = "L123", CreatedBy = 1, CreatedDate = DateTime.Now },
                new VisitorDevice { Id = 2, VisitorId = 1, DeviceId = 2, SerialNumber = "P123", CreatedBy = 1, CreatedDate = DateTime.Now },
                new VisitorDevice { Id = 3, VisitorId = 2, DeviceId = 1, SerialNumber = "L456", CreatedBy = 1, CreatedDate = DateTime.Now }
            };

            _context.PurposeOfVisits.AddRange(purposes);
            _context.OfficeLocations.AddRange(locations);
            _context.Devices.AddRange(devices);
            _context.Visitors.AddRange(visitors);
            _context.VisitorDevices.AddRange(visitorDevices);

            _context.SaveChanges();
        }

        [Test]
        public async Task AddVisitorDevice_IfDeviceIsValid_AddsDevice()
        {
            // Arrange
            var addDeviceDto = new AddVisitorDeviceDTO
            {
                VisitorId = 1,
                DeviceId = 3,
                SerialNumber = "D123"
            };

            // Act
            var result = await _repository.AddVisitorDeviceAsync(addDeviceDto);

            // Assert
            Assert.That(result, Is.Not.Null);
            Assert.That(result.SerialNumber, Is.EqualTo("D123"));
            Assert.That(result.CreatedBy, Is.EqualTo(1));
        }

        [Test]
        public async Task CreateVisitor_IfVisitorIsValid_AddsVisitor()
        {
            string validBase64ImageData = "data:image/jpeg;base64,"; // Valid 1x1 PNG image

            // Arrange
            var visitorDto = new VisitorCreationDTO
            {
                Name = "Visitor3",
                PhoneNumber = "3333333333",
                PurposeOfVisitId = 1,
                PersonInContact = "Host3",
                OfficeLocationId = 1,

                ImageData = validBase64ImageData
            };

            // Act
            var result = await _repository.CreateVisitorAsync(visitorDto);

            // Assert
            Assert.That(result, Is.Not.Null);
            Assert.That(result.Name, Is.EqualTo("Visitor3"));
            Assert.That(result.Photo, Is.Not.Null);
        }

        [Test]
        public void WhenCreatingVisitor_IfVisitorIsNull_RaiseAnError()
        {
            // Act & Assert
            Assert.ThrowsAsync<ArgumentNullException>(async () => await _repository.CreateVisitorAsync(null));
        }

        [Test]
        public async Task ReturnDistinctHostName_WhenHostNameIsCalled()
        {
            // Act
            var result = await _repository.GetPersonInContactAsync();

            // Assert
            Assert.That(result.Count(), Is.EqualTo(2));
            Assert.That(result, Does.Contain("Host1"));
            Assert.That(result, Does.Contain("Host2"));
        }

        [Test]
        public async Task GetVisitorById_WhenIdIsValid_ReturnsVisitor()
        {
            // Act
            var result = await _repository.GetVisitorByIdAsync(1);

            // Assert
            Assert.That(result, Is.Not.Null);
            Assert.That(result.Name, Is.EqualTo("Visitor1"));
        }

        [Test]
        public async Task GetVisitorById_WhenIdIsInvalid_ReturnsNull()
        {
            // Act
            var result = await _repository.GetVisitorByIdAsync(99);

            // Assert
            Assert.That(result, Is.Null);
        }

        [Test]
        public async Task GetVisitorDetails_WhenCalled_ReturnsAllVisitors()
        {
            // Act
            var result = await _repository.GetVisitorDetailsAsync();

            // Assert
            Assert.That(result.Count(), Is.EqualTo(2));
        }

          [Test]
        public async Task CreateVisitor_WhenVisitorIsValidAndHasDevices_AddsVisitorAndDevices()
        {
            string validBase64ImageData = "data:image/jpeg;base64,"; // Valid 1x1 PNG image

            // Arrange
            var visitorDto = new VisitorCreationDTO
            {
                Name = "Visitor4",
                PhoneNumber = "4444444444",
                PurposeOfVisitId = 1,
                PersonInContact = "Host4",
                OfficeLocationId = 1,
                ImageData = validBase64ImageData,
                SelectedDevice = new List<VisitorDeviceDTO>
                {
                    new VisitorDeviceDTO { DeviceId = 1, SerialNumber = "D123" },
                    new VisitorDeviceDTO { DeviceId = 2, SerialNumber = "D456" }
                }
            };

            // Act
            var result = await _repository.CreateVisitorAsync(visitorDto);

            // Assert
            Assert.That(result, Is.Not.Null);
            Assert.That(result.VisitorDevices.Count, Is.EqualTo(2));
        }

        [Test]
        public async Task GetPersonInContact_WhenCalled_ReturnsDistinctHostNamesInOrder()
        {
            // Act
            var result = await _repository.GetPersonInContactAsync();

            // Assert
            Assert.That(result.Count(), Is.EqualTo(2));
            Assert.That(result.First(), Is.EqualTo("Host1"));
        }

        [Test]
        public async Task GetVisitorById_WhenIdIsValid_ReturnsVisitorWithCorrectPhone()
        {
            // Act
            var result = await _repository.GetVisitorByIdAsync(2);

            // Assert
            Assert.That(result, Is.Not.Null);
            Assert.That(result.Phone, Is.EqualTo("2222222222"));
        }

      }
}
